INSERT INTO schema_nfe (idGrupo, campo, descricao, elemento, pai, tipo, ocorrencia, tamanho, observacao) VALUES('B03',        'cNF',	    'Código Numérico que compõe a Chave de Acesso',                                            'E',	            'B01',	    'N',	    '1-1',	        '8',        'Código numérico que compõe a Chave de Acesso. Número aleatório gerado pelo emitente para cada NF-e para evitar acessos indevidos da NF-e. (v2.0)');
INSERT INTO schema_nfe (idGrupo, campo, descricao, elemento, pai, tipo, ocorrencia, tamanho, observacao) VALUES('B04',        'natOp',	    'Descrição da Natureza da Operação',                                                       'E',	            'B01',	    'C',	    '1-1',	        '1-60',     'Informar a natureza da operação de que decorrer a saída ou a entrada, tais como: venda, compra, transferência, devolução, importação, consignação, remessa (para fins de demonstração, de industrialização ou outra), conforme previsto na alínea 'i', inciso I, art. 19 do CONVÊNIO S/Nº, de 15 de dezembro de 1970.');
INSERT INTO schema_nfe (idGrupo, campo, descricao, elemento, pai, tipo, ocorrencia, tamanho, observacao) VALUES('B05',        'indPag',	    'Indicador da forma de pagamento',                                                         'E',	            'B01',	    'N',	    '1-1',	        '1',        '0=Pagamento à vista; \n1=Pagamento a prazo; \n2=Outros.');
INSERT INTO schema_nfe (idGrupo, campo, descricao, elemento, pai, tipo, ocorrencia, tamanho, observacao) VALUES('B06',        'mod',	    'Código do Modelo do Documento Fiscal',                                                    'E',	            'B01',	    'N',	    '1-1',	        '2',        '55=NF-e emitida em substituição ao modelo 1 ou 1A; \n65=NFC-e, utilizada nas operações de venda no varejo (a critério da UF aceitar este modelo de documento).');
INSERT INTO schema_nfe (idGrupo, campo, descricao, elemento, pai, tipo, ocorrencia, tamanho, observacao) VALUES('B07',        'serie',	    'Série do Documento Fiscal',                                                               'E',	            'B01',	    'N',	    '1-1',	        '1-3',      'Série do Documento Fiscal, preencher com zeros na hipótese de a NF-e não possuir série. \n(v2.0) Série 890-899: uso exclusivo para emissão de NF-e avulsa, pelo contribuinte com seu certificado digital, através do site do Fisco (procEmi=2). (v2.0) \nSerie 900-999: uso exclusivo de NF-e emitidas no SCAN. (v2.0)');
