INSERT INTO schema_nfe (idGrupo, campo, descricao, elemento, pai, tipo, ocorrencia, tamanho, observacao) VALUES('B25a',       'indFinal',	'Indica operação com Consumidor final',                                                    'E',	            'B01',	    'N',	    '1-1',	        '1',        '0=Normal; \n1=Consumidor final;');
INSERT INTO schema_nfe (idGrupo, campo, descricao, elemento, pai, tipo, ocorrencia, tamanho, observacao) VALUES('B25b',       'indPres',	'Indicador de presença do comprador no estabelecimento comercial no momento da operação',  'E',	            'B01',	    'N',	    '1-1',	        '1',        '0=Não se aplica (por exemplo, Nota Fiscal complementar ou de ajuste); \n1=Operação presencial; \n2=Operação não presencial, pela Internet; \n3=Operação não presencial, Teleatendimento; \n4=NFC-e em operação com entrega a domicílio; \n9=Operação não presencial, outros.');
INSERT INTO schema_nfe (idGrupo, campo, descricao, elemento, pai, tipo, ocorrencia, tamanho, observacao) VALUES('B26',        'procEmi',	'Processo de emissão da NF-e',                                                             'E',	            'B01',	    'N',	    '1-1',	        '1',        '0=Emissão de NF-e com aplicativo do contribuinte; \n1=Emissão de NF-e avulsa pelo Fisco; \n2=Emissão de NF-e avulsa, pelo contribuinte com seu certificado digital, através do site do Fisco; \n3=Emissão NF-e pelo contribuinte com aplicativo fornecido pelo Fisco.');
