INSERT INTO schema_nfe (idGrupo, campo, descricao, elemento, pai, tipo, ocorrencia, tamanho, observacao) VALUES('B06',        'mod',	    'Código do Modelo do Documento Fiscal',                                                    'E',	            'B01',	    'N',	    '1-1',	        '2',        '55=NF-e emitida em substituição ao modelo 1 ou 1A; \n65=NFC-e, utilizada nas operações de venda no varejo (a critério da UF aceitar este modelo de documento).');
INSERT INTO schema_nfe (idGrupo, campo, descricao, elemento, pai, tipo, ocorrencia, tamanho, observacao) VALUES('B07',        'serie',	    'Série do Documento Fiscal',                                                               'E',	            'B01',	    'N',	    '1-1',	        '1-3',      'Série do Documento Fiscal, preencher com zeros na hipótese de a NF-e não possuir série. \n(v2.0) Série 890-899: uso exclusivo para emissão de NF-e avulsa, pelo contribuinte com seu certificado digital, através do site do Fisco (procEmi=2). (v2.0) \nSerie 900-999: uso exclusivo de NF-e emitidas no SCAN. (v2.0)');
