INSERT INTO schema_nfe (idGrupo, campo, descricao, elemento, pai, tipo, ocorrencia, tamanho, observacao) VALUES('B21',        'tpImp',	    'Formato de Impressão do DANFE',                                                           'E',	            'B01',	    'N',	    '1-1',	        '1',        '0=Sem geração de DANFE; \n1=DANFE normal, Retrato; \n2=DANFE normal, Paisagem; \n3=DANFE Simplificado; \n4=DANFE NFC-e; \n5=DANFE NFC-e em mensagem eletrônica (o envio de mensagem eletrônica pode ser feita de forma simultânea com a impressão do DANFE; usar o tpImp=5 quando esta for a única forma de disponibilização do DANFE).');
INSERT INTO schema_nfe (idGrupo, campo, descricao, elemento, pai, tipo, ocorrencia, tamanho, observacao) VALUES('B22',        'tpEmis',	    'Tipo de Emissão da NF-e',                                                                 'E',	            'B01',	    'N',	    '1-1',	        '1',        '1=Emissão normal (não em contingência); \n2=Contingência FS-IA, com impressão do DANFE em formulário de segurança; \n3=Contingência SCAN (Sistema de Contingência do Ambiente Nacional); \n4=Contingência DPEC (Declaração Prévia da Emissão em Contingência); \n5=Contingência FS-DA, com impressão do DANFE em formulário de segurança; \n6=Contingência SVC-AN (SEFAZ Virtual de Contingência do AN); \n7=Contingência SVC-RS (SEFAZ Virtual de Contingência do RS); \n9=Contingência off-line da NFC-e (as demais opções de contingência são válidas também para a NFC-e); Para a NFC-e somente estão disponíveis e são válidas as opções de contingência 5 e 9.');
INSERT INTO schema_nfe (idGrupo, campo, descricao, elemento, pai, tipo, ocorrencia, tamanho, observacao) VALUES('B23',        'cDV',	    'Dígito Verificador da Chave de Acesso da NF-e',                                           'E',	            'B01',	    'N',	    '1-1',	        '1',        'Informar o DV da Chave de Acesso da NF-e, o DV será calculado com a aplicação do algoritmo módulo 11 (base 2,9) da Chave de Acesso. (vide item 5 do Manual de Orientação)');
